import React from "react";
import SuccessLogo from '../images/Success.png'
import failLogo from '../images/Fail.png'

const InfoTooltip = () => {
  const textError =  (
    `${isError ? 'Вы успешно зарегистрировались!' :'Что-то пошло не так! Попробуйте еще раз.'}`
  )
  const imageError = (`${isError ? SuccessLogo : failLogo}`)


  return(
    <div className="popup popup_active">
      <div className="popup__container">
        <button className="popup__close" type="button"/>
        <div className="infotooltip">
          <img className="infotooltip__image" src={imageError} alt="логотип"/>
          <h3 className="popup__title">{textError}</h3>
        </div>
      </div>
    </div>
  )
}

export default InfoTooltip
